import { useState, useEffect } from 'react';

function getTomorrow() {
    let today = new Date();
    let tomorrow = new Date(today);
    tomorrow.setDate(tomorrow.getDate()+1)
    tomorrow.setHours(0,0,0,0);
    return tomorrow.getTime();
}

function render(time) {
    let hh = Math.floor((time % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
    let mm = Math.floor((time % (1000 * 60 * 60)) / (1000 * 60));
    let ss = Math.floor((time % (1000 * 60)) / 1000); 
    let leadZeroTime = [hh, mm, ss].map(t => t < 10 ? `0${t}` : t);
    return <span>{leadZeroTime[0]}:{leadZeroTime[1]}:{leadZeroTime[2]}</span>;
}

export default function Countdown() {
    const tmrw = getTomorrow();
    const [time, setTime] = useState(0);

    useEffect(() => {
        let countdown;
        countdown = setInterval(() => {
            let today = new Date().getTime();
            setTime(tmrw - today);
        }, 1000);
        return () => clearInterval(countdown);
    });

    return (
        <div>
            { render(time) }
        </div>
    );
}
