import { useState, useEffect, useRef } from "react";

export default function Counter({ id, index, rgb }) {
    let red = useRef(id === index ? 0 : rgb[0]);
    let green = useRef(rgb[1]);
    let blue = id === index ? 0 : rgb[2];
    const [opacity, setOpacity] = useState(0.21);

    useEffect(() => {
        if (id === index) {
            setOpacity(1);
            let interval = setInterval(() => {
                if (red === 255 ) {
                    clearInterval(interval);
                } else {
                    green -= 1;
                    red += 1;
                }
            }, 100);
            return () => clearInterval(interval);
        }
    }, [id, index, red]);

    return (
        <span 
            className="miniplane" 
            style={{backgroundColor: `rgba(${red}, ${green}, ${blue}, ${opacity})`}} 
            key={id}>
        </span>
    );
}
