import { useState, useEffect } from 'react';
import Plane from './Plane';
import Answers from './Answers';

function compDay() {
    let today = new Date();
    let yyyy = today.getFullYear();
    let mm = String(today.getMonth() + 1).padStart(2, '0');
    let dd = String(today.getDate()).padStart(2, '0');
    return parseInt(`${yyyy}${mm}${dd}`);
}

export default function Game() {
    const [data, setData] = useState();
    const [index, setIndex] = useState(0);
  
    useEffect(() => {
      fetch("/api/game", {
        method: "POST",
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ seed: compDay() })
      })
      .then(res => res.json())
      .then(data => setData(data))
    }, []);

    const createCounters = () => {
        const counters = [];
        for (let i = 0; i < 10; i++) {
            counters.push(<span className="miniplane" key={i}></span>);
        }
        return counters;
    }

    return (
        <div id="game" className="animate__animated animate__faster">
            <div id="timer"></div>
            <div id="counter" className="miniplane-container">
                { createCounters }
            </div>
            <div className="img-container">
                {data && <Plane data={data.data} index={index} />}
            </div>
            <div id="answers">
                {data && <Answers data={data.data} index={index} />}
            </div>
        </div>
    );
}
