import { useState, useEffect } from "react";

export default function Counter({ id, index }) {
    const [red, setRed] = useState(id === index ? 0 : 255);
    const [green, setGreen] = useState(255);
    const blue = id <= index ? 0 : 255;
    const [opacity, setOpacity] = useState(0.21);

    useEffect(() => {
        if (id === index) {
            setOpacity(1);
            let interval = setInterval(() => {
                setGreen(g => g - 1);
                setRed(r => r + 1);
            }, 100);
            return () => clearInterval(interval);
        }
    }, [id, index]);

    return (
        <span 
            className="miniplane" 
            style={{backgroundColor: `rgba(${red}, ${green}, ${blue}, ${opacity})`}} 
            key={id}>
        </span>
    );
}
