import { useState } from 'react';
import Timer from "./Timer";
import Counter from './Counter';
import Plane from './Plane';
import AnswerButton from './AnswerButton';
import styles from '../styles/Game.module.css';

function findAnswer(q) {
    return q[0];
}

export default function Game({ data, endGame }) {
    const [status, setStatus] = useState(true);
    const [addTime, setAddTime] = useState(false);
    const [minicolors, setMinicolors] = useState([]);
    const [animate, setAnimate] = useState(false);
    const [incCounter, setIncCounter] = useState(false);
    const [stopCount, setStopCount] = useState(false);
    const [disabled, setDisabled] = useState(false);
    const [index, setIndex] = useState(0);
    const answer = data[index].filter(findAnswer)[0];

    const subTime = () => {
        setAddTime(false);
    }

    const unanimate = () => {
        setAnimate(false);
    }

    const decCounter = () => {
        setIncCounter(false);
    }

    const getMiniPlaneColors = (rgb) => {
        setMinicolors((prev) => [...prev, rgb]);
        console.log(minicolors);
    }

    const getTime = (t) => {
        endGame(t, minicolors);
    }

    const checkAnswer = (e) => {
        let answer = data[index].filter(findAnswer)[0];
        if (e.target.value === answer[1]) {
            setDisabled(true);
            setStopCount(true);
            if (index === data.length - 1) {
                setStatus(false);
            } else {
                setTimeout(() => {
                    setIndex(i => i + 1);
                    setDisabled(false);
                    setStopCount(false);
                }, 1000);
            }
        } else {
            e.target.disabled = true;
            setAddTime(true);
            setIncCounter(true);
            if (!animate) {
                setAnimate(true);
            } else {
                setAnimate(false);
                setTimeout(() => {
                    setAnimate(true);
                }, 10);
            }
        }
    }

    return (
        <div className="animate__animated animate__faster">
            <Timer status={status} addTime={addTime} subTime={subTime} animate={animate} unanimate={unanimate} getTime={getTime} />
            <div className={styles.miniplane_container}>
                {data && 
                    data.map((d, i) =>
                        <Counter key={i} id={i} index={index} incCounter={incCounter} decCounter={decCounter} stopCount={stopCount} getMiniPlaneColors={getMiniPlaneColors} />
                    )
                }
            </div>
            {data && <Plane data={answer[2]} />}
            <div className={styles.answers}>
                {data && 
                    data[index].map((d, i) =>
                        <AnswerButton key={i} id={d[1]} answer={answer} index={index} disabled={disabled} checkAnswer={checkAnswer} />
                    )
                }
            </div>
        </div>
    );
}
