import { useState, useEffect } from "react";
import styles from '../styles/Counter.module.css';

export default function Counter({ id, index, addTime, subTime }) {
    const [used, setUsed] = useState(false);
    const [red, setRed] = useState(0);
    const [green, setGreen] = useState(255);
    const [blue, setBlue] = useState(255);
    const [opacity, setOpacity] = useState(0.21);

    useEffect(() => {
        if (id === index) {
            setUsed(true);
            setBlue(0);
            setOpacity(1);
            let interval = setInterval(() => {
                if (red === 255 ) {
                    clearInterval(interval);
                } else {
                    setGreen(g => addTime ? g - 75 : g - 1);
                    setRed(r => addTime ? r + 75 : r + 1);
                    subTime();
                }
            }, 100);
            return () => clearInterval(interval);
        }
    }, [id, index, red, addTime, subTime]);

    return (
        <span 
            className={styles.miniplane}
            style={{backgroundColor: `rgba(${used ? red : 255}, ${green}, ${blue}, ${opacity})`}} 
            key={id}
        ></span>
    );
}
