export default function Answers({ data, checkAnswer, isRight, isWrong }) {
    return (
        <>
            {data.map(x => (
                <button 
                    className={`
                        answer-button 
                        animate__animated 
                        animate__faster 
                        ${isRight ? 'right' : ''}
                        ${isWrong ? 'wrong' : ''} 
                    `} 
                    key={x[1]} 
                    value={x[1]}
                    onClick={e => checkAnswer(e)}
                > {x[1]} </button>
            ))}
        </>
    );
}
