import React, { useState, useEffect } from 'react';
import Navbar from "./navbar.js";
import Pregame from "./pregame.js";
import Game from "./game.js";
import Postgame from './postgame.js';
import Info from './info.js';
import Stats from './stats.js';
import './index.css';

function App() {
  const [getData, setData] = useState({})

  useEffect(() => {
    fetch("/api/main").then(res => res.json()).then(data => {
      setData(data);
    });
  });

  return (
    <div>
      <Navbar />
      <script type="text/javascript">
        const data = { getData.data };
        const day = { getData.day };
      </script>
      <Pregame />
      <Game />
      <Postgame />
      <Info />
      <Stats />
      <div id="results" class="share-overlay animate__animated animate__faster">
        <button class="close icon" onclick="closeResults()">
          <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="#fff" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"><line x1="18" y1="6" x2="6" y2="18"></line><line x1="6" y1="6" x2="18" y2="18"></line></svg>
        </button>
      </div>
      <div id="toast" class="animate__animated animate__faster"></div>
      <script type="text/javascript" src='html2canvas.min.js'></script>
      <script type="text/javascript" src="timer.js"></script>
      <script type="text/javascript" src="main.js"></script>
    </div>
  );
}

export default App;
