import { useState, useEffect } from 'react';

function render(time) {
    var ms = parseInt((time%1000)/10);
    var ss = parseInt((time/1000)%60);
    var mm = parseInt((time/(1000*60))%60);
    var leadZeroTime = [mm, ss, ms].map(t => t < 10 ? `0${t}` : t);
    return (<span>{leadZeroTime[0]}:{leadZeroTime[1]}.{leadZeroTime[2]}</span>);
}

export default function Timer() {
    const [time, setTime] = useState(0);
    const [running, setRunning] = useState(false);
    
    useEffect(() => {
        let interval;
        if (running) {
            let offset = Date.now();
            interval = setInterval(() => {
                let now = Date.now(), d = now - offset;
                offset = now;
                setTime((clock) => clock + d)
            });
        }
        return () => clearInterval(interval);
    }, [running]);

    return (
        <div>
            <div>
                { render(time) }
            </div>
            <div>
                <button onClick={() => setRunning(true)}>Start</button>
                <button onClick={() => setRunning(false)}>Stop</button>
            </div>
        </div>
    )
}
