import { useState, useEffect } from 'react';
import Plane from './Plane';
import AnswerButton from './AnswerButton';

function compDay() {
    let today = new Date();
    let yyyy = today.getFullYear();
    let mm = String(today.getMonth() + 1).padStart(2, '0');
    let dd = String(today.getDate()).padStart(2, '0');
    return parseInt(`${yyyy}${mm}${dd}`);
}

function findAnswer(a) {
    return a[0];
}

export default function Game() {
    const [day, setDay] = useState();
    const [data, setData] = useState();
    const [index, setIndex] = useState(0);
    const [answer, setAnswer] = useState();
  
    useEffect(() => {
        fetch("/api/game", {
            method: "POST",
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ seed: compDay() })
        })
        .then(res => res.json())
        .then(data => {
            setDay(data.day);
            setData(data.data);
            setAnswer(data.data[index].filter(findAnswer)[0]);
        })
    }, [index]);

    const createCounters = () => {
        const counters = [];
        for (let i = 0; i < 10; i++) {
            counters.push(<span className="miniplane" key={i}></span>);
        }
        return counters;
    }

    const checkAnswer = (e) => {
        if (e === answer[1]) {
            setIndex(index + 1);
        }
    }

    return (
        <div id="game" className="animate__animated animate__faster">
            <div id="timer"></div>
            <div id="counter" className="miniplane-container">
                {createCounters()}
            </div>
            <div className="img-container">
                {data && <Plane data={data[index].filter(findAnswer)[0][2]} />}
            </div>
            <div id="answers">
                {data && 
                    <>
                        <AnswerButton id={data[index][0][1]} answer={answer} checkAnswer={checkAnswer} />
                        <AnswerButton id={data[index][1][1]} answer={answer} checkAnswer={checkAnswer} />
                        <AnswerButton id={data[index][2][1]} answer={answer} checkAnswer={checkAnswer} />
                        <AnswerButton id={data[index][3][1]} answer={answer} checkAnswer={checkAnswer} />
                    </>
                }
            </div>
        </div>
    );
}
