import Countdown from "./Countdown";
import styles from '../styles/Postgame.module.css';

function getTimeFromMs(t) {
    let mm = parseInt((t / (1000 * 60)) % 60);
    let ss = parseInt((t / 1000) % 60);
    let ms = parseInt((t % 1000) / 10);
    let leadZeroTime = [mm, ss, ms].map(time => time < 10 ? `0${time}` : time);
    return <>{leadZeroTime[0]}:{leadZeroTime[1]}.{leadZeroTime[2]}</>;
}

export default function Postgame({ completionTime }) {
    const createCounters = () => {
        const counters = [];
        for (let i = 0; i < 10; i++) {
            counters.push(<span key={i} className="miniplane"></span>);
        }
        return counters;
    }

    return (
        <div className="overlay_content animate__animated animate__faster">
            <div className="overlay_container">
                <h1>Completion Time</h1>
                <span className={styles.completion_time}>
                    {getTimeFromMs(completionTime)}
                </span>
            </div>
            <div className="overlay_container">
                <h1>Answers</h1>
                <div>
                    { createCounters() }
                </div>
            </div>
            <div className={styles.footer}>
                <div className={styles.countdown}>
                    <h1>Next Planes</h1>
                    <Countdown />
                </div>
                <div className={styles.share}>
                    <button className={styles.share_button} onClick={ window['createShareable'] }>
                        Share
                        <span className="icon">
                            <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="#fff" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"><circle cx="18" cy="5" r="3"></circle><circle cx="6" cy="12" r="3"></circle><circle cx="18" cy="19" r="3"></circle><line x1="8.59" y1="13.51" x2="15.42" y2="17.49"></line><line x1="15.41" y1="6.51" x2="8.59" y2="10.49"></line></svg>
                        </span>
                    </button>
                </div>
            </div>
        </div>
    );
}
