import { useState } from "react";

export default function AnswerButton({ data, status, checkAnswer }) {
    const [disabled, setDisabled] = useState(false);

    return (
        <>
            <button 
                className={`
                    answer-button 
                    animate__animated 
                    animate__faster
                    ${status}
                `} 
                key={data} 
                value={data}
                disabled={disabled}
                onClick={e => checkAnswer(e)}
            > {data} </button>
        </>
    );
}
