import { useState, useEffect } from "react";
import styles from '../styles/AnswerButton.module.css';

export default function AnswerButton({ id, answer, index, disabled, checkAnswer }) {
    const [disable, setDisable] = useState(disabled);
    const [status, setStatus] = useState('');

    const checkGuess = (e) => {
        if (e === answer[1]) {
            setStatus(styles.correct);
        } else {
            setDisable(true);
            setStatus(styles.wrong);
        }
    }

    useEffect(() => {
        setStatus('');
        setDisable(false);
    }, [index]);

    return (
        <>
            <button 
                className={`${styles.answerbutton} animate__animated animate__faster ${status}`} 
                key={id} 
                value={id} 
                disabled={disable} 
                onClick={(e) => {checkAnswer(e.target.value); checkGuess(e.target.value)}}
            > {id} </button>
        </>
    );
}
