import { useState } from 'react';
import Timer from "./Timer";
import Counter from './Counter';
import Plane from './Plane';
import AnswerButton from './AnswerButton';
import styles from '../styles/Game.module.css';

function findAnswer(a) {
    return a[0];
}

export default function Game({ data }) {
    const [status, setStatus] = useState(true);
    const [addTime, setAddTime] = useState(false);
    const [animate, setAnimate] = useState(false);
    const [incCounter, setIncCounter] = useState(false);
    const [stopCount, setStopCount] = useState(false);
    const [disabled, setDisabled] = useState(false);
    const [index, setIndex] = useState(0);
    const answer = data[index].filter(findAnswer)[0];

    const subTime = () => {
        setAddTime(false);
    }

    const unanimate = () => {
        setAnimate(false);
    }

    const decCounter = () => {
        setIncCounter(false);
    }

    const checkAnswer = (e) => {
        let answer = data[index].filter(findAnswer)[0];
        if (e.target.value === answer[1]) {
            setDisabled(true);
            setStopCount(true);
            if (index === data.length - 1) {
                setStatus(false);
            } else {
                setTimeout(() => {
                    setIndex(i => i + 1);
                    setDisabled(false);
                    setStopCount(false);
                }, 1000);
            }
        } else {
            e.target.disabled = true;
            setAddTime(true);
            setIncCounter(true);
            if (!animate) {
                setAnimate(true);
            } else {
                setAnimate(false);
                setTimeout(() => {
                    setAnimate(true);
                }, 10);
            }
        }
    }

    return (
        <div className="animate__animated animate__faster">
            <Timer status={status} addTime={addTime} subTime={subTime} animate={animate} unanimate={unanimate} />
            <div id="counter" className="miniplane-container">
                <Counter id={0} index={index} incCounter={incCounter} decCounter={decCounter} stopCount={stopCount} />
                <Counter id={1} index={index} incCounter={incCounter} decCounter={decCounter} stopCount={stopCount} />
                <Counter id={2} index={index} incCounter={incCounter} decCounter={decCounter} stopCount={stopCount} />
                <Counter id={3} index={index} incCounter={incCounter} decCounter={decCounter} stopCount={stopCount} />
                <Counter id={4} index={index} incCounter={incCounter} decCounter={decCounter} stopCount={stopCount} />
                <Counter id={5} index={index} incCounter={incCounter} decCounter={decCounter} stopCount={stopCount} />
                <Counter id={6} index={index} incCounter={incCounter} decCounter={decCounter} stopCount={stopCount} />
                <Counter id={7} index={index} incCounter={incCounter} decCounter={decCounter} stopCount={stopCount} />
                <Counter id={8} index={index} incCounter={incCounter} decCounter={decCounter} stopCount={stopCount} />
                <Counter id={9} index={index} incCounter={incCounter} decCounter={decCounter} stopCount={stopCount} />
            </div>
            <div className={styles.img_container}>
                {data && <Plane data={answer[2]} />}
            </div>
            <div className={styles.answers}>
                {data && 
                    <>
                        <AnswerButton id={data[index][0][1]} answer={answer} index={index} disabled={disabled} checkAnswer={checkAnswer} />
                        <AnswerButton id={data[index][1][1]} answer={answer} index={index} disabled={disabled} checkAnswer={checkAnswer} />
                        <AnswerButton id={data[index][2][1]} answer={answer} index={index} disabled={disabled} checkAnswer={checkAnswer} />
                        <AnswerButton id={data[index][3][1]} answer={answer} index={index} disabled={disabled} checkAnswer={checkAnswer} />
                    </>
                }
            </div>
        </div>
    );
}
