import { useState, useEffect } from "react";

export default function Counter({ id, index }) {
    const [red, setRed] = useState(0);
    const [green, setGreen] = useState(0);
    const [opacity, setOpacity] = useState(0.21);

    useEffect(() => {
        if (id === index) {
            setGreen(255);
            setOpacity(1);
            let interval = setInterval(() => {
                if (red === 255 ) {
                    clearInterval(interval);
                } else {
                    setGreen(green => green - 1);
                    setRed(red => red + 1)
                }
            }, 1000);
        }
    });

    return (
        <span 
            className="miniplane" 
            style={{backgroundColor: `rgba(${red}, ${green}, 0, ${opacity})`}} 
            key={id}>
        </span>
    );
}
