import { useState, useEffect } from "react";

export default function Counter({ id, index, rgb }) {
    const [red, setRed] = useState(id === index ? 0 : rgb[0]);
    const [green, setGreen] = useState(rgb[1]);
    const [blue, setBlue] = useState(rgb[2]);
    const [opacity, setOpacity] = useState(0.21);

    useEffect(() => {
        if (id === index) {
            setBlue(0);
            setOpacity(1);
            let interval = setInterval(() => {
                if (red === 255 ) {
                    clearInterval(interval);
                } else {
                    setGreen(g => g - 1);
                    setRed(r => r + 1);
                }
            }, 100);
            return () => clearInterval(interval);
        }
    }, [id, index, red]);

    return (
        <span 
            className="miniplane" 
            style={{backgroundColor: `rgba(${red}, ${green}, ${blue}, ${opacity})`}} 
            key={id}>
        </span>
    );
}
