function Game() {
    const createCounters = () => {
        const counters = [];
        for (let i = 0; i < 10; i++) {
            counters.push(<span class="miniplane"></span>);
        }
        return counters;
    }

    const createAnswers = () => {
        const answers = [];
        for (let i = 0; i < 4; i++) {
            answers.push(<button class="answer-button animate__animated animate__faster" onClick="getAnswer(this)"></button>);
        }
        return answers;
    };

    return (
        <div id="game" class="overlay animate__animated animate__faster">
            <div id="timer"></div>
            <div id="counter" class="miniplane-container">
                { createCounters }
            </div>
            <div class="img-container">
                <a id="link" href="" target="_blank" alt="plane.jpg"><img id='image'/></a>
            </div>
            <div id="answers">
                { createAnswers }
            </div>
        </div>
    );
}

export default Game;
