import { useState, useEffect } from "react";

export default function Counter({ key }) {
    const [red, setRed] = useState(0);
    const [green, setGreen] = useState(255);

    useEffect(() => {
        let interval = setInterval(() => {
            if (red === 255 ) {
                clearInterval(interval);
            } else {
                setGreen(green => green - 1);
                setRed(red => red + 1)
            }
        }, 100);
    });

    return (
        <span className="miniplane" style={`rbg(${red},${green},0)`} key={key}></span>
    );
}
