import { useState } from "react";

export default function AnswerButton({ id, answer, checkAnswer }) {
    const [disabled, setDisabled] = useState('');
    const [status, setStatus] = useState('');

    const checkGuess = (e) => {
        if (e === answer[1]) {
            setStatus('correct');
        } else {
            setDisabled(true);
            setStatus('wrong');
        }
    }

    return (
        <>
            <button 
                className={`answer-button animate__animated animate__faster ${status}`} 
                key={id} 
                value={id} 
                disabled={!disabled} 
                onClick={(e) => {checkAnswer(e.target.value); checkGuess(e.target.value)}}
            > {id} </button>
        </>
    );
}
