import { useState, useEffect } from 'react';
import styles from '../styles/Timer.module.css';

function render(time) {
    let mm = parseInt((time / (1000 * 60)) % 60);
    let ss = parseInt((time / 1000) % 60);
    let ms = parseInt((time % 1000) / 10);
    let leadZeroTime = [mm, ss, ms].map(t => t < 10 ? `0${t}` : t);
    return <>{leadZeroTime[0]}:{leadZeroTime[1]}.{leadZeroTime[2]}</>;
}

export default function Timer({ status, addTime, subTime, animate, unanimate }) {
    const [time, setTime] = useState(0);
    const [classn, setClassn] = useState(styles.static);
    
    useEffect(() => {
        let interval;
        if (status) {
            let offset = Date.now();
            interval = setInterval(() => {
                let now = Date.now(), d = now - offset;
                offset = now;
                setTime((clock) => addTime ? clock + d + 10000 : clock + d);
                subTime();
            });
        }
        return () => clearInterval(interval);
    }, [status, addTime, subTime]);

    useEffect(() => {
        if (animate && classn !== styles.animate) {
            setClassn(styles.animate);
            // unanimate();
        } else if (animate && classn === styles.animate) {
            setClassn(styles.static);
            setClassn(styles.animate);
            // unanimate();
        } else {
            unanimate();
        }
    }, [animate, classn, unanimate])

    return (
        <div className={styles.timer}>
            <span className={classn}>
                { render(time) }
            </span>
        </div>
    );
}
