import { useState } from "react";

export default function AnswerButton({ id, checkAnswer }) {
    const [disabled, setDisabled] = useState(false);
    const [status, setStatus] = useState('');

    return (
        <>
            <button 
                className={`answer-button animate__animated animate__faster ${status}`} 
                key={id} 
                value={id}
                disabled={disabled}
                onClick={e => checkAnswer(e)}
            > {id} </button>
        </>
    );
}
