import { useState, useEffect } from 'react';
import Timer from "./Timer";
import Counter from './Counter';
import Plane from './Plane';
import AnswerButton from './AnswerButton';

function findAnswer(a) {
    return a[0];
}

export default function Game({ data }) {
    const [index, setIndex] = useState(0);
    const answer = data[index].filter(findAnswer)[0];

    const checkAnswer = (e) => {
        console.log(colors[index]);
        let answer = data[index].filter(findAnswer)[0];
        if (e === answer[1]) {
            setIndex(i => i + 1);
        }
    }

    const colors = [
        {'r': 0, 'g': 255, 'b': 255, 'a': 1},
        {'r': 255, 'g': 255, 'b': 255, 'a': 0.21},
        {'r': 255, 'g': 255, 'b': 255, 'a': 0.21},
        {'r': 255, 'g': 255, 'b': 255, 'a': 0.21},
        {'r': 255, 'g': 255, 'b': 255, 'a': 0.21},
        {'r': 255, 'g': 255, 'b': 255, 'a': 0.21},
        {'r': 255, 'g': 255, 'b': 255, 'a': 0.21},
        {'r': 255, 'g': 255, 'b': 255, 'a': 0.21},
        {'r': 255, 'g': 255, 'b': 255, 'a': 0.21},
        {'r': 255, 'g': 255, 'b': 255, 'a': 0.21}
    ]

    useEffect(() => {
        let interval = setInterval(() => {
            if (colors[index].r === 255 ) {
                clearInterval(interval);
            } else {
                colors[index].g -= 1;
                colors[index].r += 1;
            }
        }, 100);
        return () => clearInterval(interval);
    }, [index, colors]);

    return (
        <div id="game" className="animate__animated animate__faster">
            <div id="timer">
                <Timer status={true} />
            </div>
            <div id="counter" className="miniplane-container">
                <Counter id={0} rgba={colors[0]} />
                <Counter id={1} rgba={colors[1]} />
                <Counter id={2} rgba={colors[2]} />
                <Counter id={3} rgba={colors[3]} />
                <Counter id={4} rgba={colors[4]} />
                <Counter id={5} rgba={colors[5]} />
                <Counter id={6} rgba={colors[6]} />
                <Counter id={7} rgba={colors[7]} />
                <Counter id={8} rgba={colors[8]} />
                <Counter id={9} rgba={colors[9]} />
            </div>
            <div className="img-container">
                {data && <Plane data={answer[2]} />}
            </div>
            <div id="answers">
                {data && 
                    <>
                        <AnswerButton id={data[index][0][1]} answer={answer} index={index} checkAnswer={checkAnswer} />
                        <AnswerButton id={data[index][1][1]} answer={answer} index={index} checkAnswer={checkAnswer} />
                        <AnswerButton id={data[index][2][1]} answer={answer} index={index} checkAnswer={checkAnswer} />
                        <AnswerButton id={data[index][3][1]} answer={answer} index={index} checkAnswer={checkAnswer} />
                    </>
                }
            </div>
        </div>
    );
}
