import { useState, useEffect } from 'react';
import Timer from './Timer.js';

export default function Game({ day }) {
    const createCounters = () => {
        const counters = [];
        for (let _ = 0; _ < 10; _++) {
            counters.push(<span className="miniplane"></span>);
        }
        return counters;
    }

    const createAnswers = () => {
        const answers = [];
        for (let _ = 0; _ < 4; _++) {
            answers.push(<button className="answer-button animate__animated animate__faster" onClick={ window['getAnswer(this)'] }></button>);
        }
        return answers;
    };

    return (
        <div id="game" className="overlay animate__animated animate__faster">
            <div id="timer"></div>
            <div id="counter" className="miniplane-container">
                { createCounters }
            </div>
            <div className="img-container">
                <a id="link" href="" target="_blank"><img id='image' alt="plane.jpg"/></a>
            </div>
            <div id="answers">
                { createAnswers }
            </div>
        </div>
    );
}
