import { useState, useEffect } from "react";
import styles from '../styles/AnswerButton.module.css';

export default function AnswerButton({ id, answer, index, checkAnswer }) {
    const [disabled, setDisabled] = useState(false);
    const [status, setStatus] = useState('');

    const checkGuess = (e) => {
        if (e === answer[1]) {
            setStatus('correct');
        } else {
            setDisabled(true);
            setStatus('wrong');
        }
    }

    useEffect(() => {
        setStatus('');
    }, [index]);

    return (
        <>
            <button 
                className={`answer-button animate__animated animate__faster ${status}`} 
                key={id} 
                value={id} 
                disabled={disabled} 
                onClick={(e) => {checkAnswer(e.target.value); checkGuess(e.target.value)}}
            > {id} </button>
        </>
    );
}
