import { useState, useEffect } from 'react';
import Timer from "./Timer";
import Counter from './Counter';
import Plane from './Plane';
import AnswerButton from './AnswerButton';

function compDay() {
    let today = new Date();
    let yyyy = today.getFullYear();
    let mm = String(today.getMonth() + 1).padStart(2, '0');
    let dd = String(today.getDate()).padStart(2, '0');
    return parseInt(`${yyyy}${mm}${dd}`);
}

function findAnswer(a) {
    return a[0];
}

export default function Game() {
    const [day, setDay] = useState();
    const [data, setData] = useState();
    const [index, setIndex] = useState(0);
    const [gameState, setGameState] = useState({});
    const [statistics, setStatistics] = useState({});
  
    useEffect(() => {
        let gameState = JSON.parse(localStorage.getItem('gameState'));
        let statistics = JSON.parse(localStorage.getItem('statistics'));
        fetch("/api/game", {
            method: "POST",
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ seed: compDay() })
        })
        .then(res => res.json())
        .then(data => {
            setDay(data.day);
            setData(data.data);
        })
    }, []);

    const checkAnswer = (e) => {
        let answer = data[index].filter(findAnswer)[0];
        if (e === answer[1]) {
            setIndex(index + 1);
        }
    }

    return (
        <div id="game" className="animate__animated animate__faster">
            <div id="timer">
                <Timer />
            </div>
            <div id="counter" className="miniplane-container">
                <Counter key={0} />
                <Counter key={1} />
                <Counter key={2} />
                <Counter key={3} />
                <Counter key={4} />
                <Counter key={5} />
                <Counter key={6} />
                <Counter key={7} />
                <Counter key={8} />
                <Counter key={9} />
            </div>
            <div className="img-container">
                {data && <Plane data={data[index].filter(findAnswer)[0][2]} />}
            </div>
            <div id="answers">
                {data && 
                    <>
                        <AnswerButton id={data[index][0][1]} answer={data[index].filter(findAnswer)[0]} index={index} checkAnswer={checkAnswer} />
                        <AnswerButton id={data[index][1][1]} answer={data[index].filter(findAnswer)[0]} index={index} checkAnswer={checkAnswer} />
                        <AnswerButton id={data[index][2][1]} answer={data[index].filter(findAnswer)[0]} index={index} checkAnswer={checkAnswer} />
                        <AnswerButton id={data[index][3][1]} answer={data[index].filter(findAnswer)[0]} index={index} checkAnswer={checkAnswer} />
                    </>
                }
            </div>
        </div>
    );
}
