import { useState, useEffect } from 'react';

function compDay() {
    let today = new Date();
    let yyyy = today.getFullYear();
    let mm = String(today.getMonth() + 1).padStart(2, '0');
    let dd = String(today.getDate()).padStart(2, '0');
    return parseInt(`${yyyy}${mm}${dd}`);
}

function findAnswer(a) {
    return a[0];
}

export default function Game() {
    const [data, setData] = useState({});
    const [index, setIndex] = useState(0);
  
    useEffect(() => {
      fetch("/api/game", {
        method: "POST",
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ seed: compDay() })
      })
      .then(res => res.json())
      .then(data => setData(data))
    }, []);

    const createCounters = () => {
        const counters = [];
        for (let _ = 0; _ < 10; _++) {
            counters.push(<span className="miniplane"></span>);
        }
        return counters;
    }

    return (
        <div id="game" className="animate__animated animate__faster">
            <div id="timer"></div>
            <div id="counter" className="miniplane-container">
                { createCounters }
            </div>
            <div className="img-container">
                <a id="link" href={ data[index].filter(findAnswer).link } target="_blank" rel="noreferrer">
                    <img src={ data[index].filter(findAnswer).pic } alt="plane.jpg"/>
                </a>
            </div>
            <div id="answers">
                {
                    data[index].map(
                        x => <button className="answer-button animate__animated animate__faster" value={ x[1] }>{ x[1] }</button>
                    )
                }
            </div>
        </div>
    );
}
