import { useState, useEffect } from "react";

export default function Counter({ id, index }) {
    const [used, setUsed] = useState(false);
    const [opacity, setOpacity] = useState(0.21);
    const [red, setRed] = useState(used ? 255 : 0);
    const [green, setGreen] = useState(255);
    const blue = id === index ? 0 : 255;

    useEffect(() => {
        if (id === index) {
            setUsed(true);
            setOpacity(1);
            let interval = setInterval(() => {
                if (red === 255 ) {
                    clearInterval(interval);
                } else {
                    setGreen(g => g - 1);
                    setRed(r => r + 1);
                }
            }, 100);
            return () => clearInterval(interval);
        }
    }, [id, index, red]);

    return (
        <span 
            className="miniplane" 
            style={{backgroundColor: `rgba(${red}, ${green}, ${blue}, ${opacity})`}} 
            key={id}>
        </span>
    );
}
